package decision_making_statement;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5;
		switch (i) {
		case 1: {
			System.out.println("the case 1");
			break;
		}
		case 5: {
			System.out.println("the case 5");

		}
		default:
			System.out.println("this the statement");
			break;

		}

	}

}
